mapper.update:

update job


        j_name = #{j_name},


        j_description = #{j_description},


        j_salary = #{j_salary},


        j_hours = #{j_hours}


        j_require = #{j_require}


where j_id = #{j_id}

mapper.select:
select * from Job


        u_id = #{u_id}


        and j_name like concat('%',#{j_name},'%')

SELECT j_id, j_name,j_description,j_salary,j_hours,j_require,p_name,p_telephone,p_address,p_email,p_introduction FROM job JOIN publisher ON job.u_id = publisher.u_id WHERE j_id IN
#{j_id}
AND j_id = #{j_id}
